mapper.select:
select t1.*,t2.`name` AS teacherName,t3.`name` AS schoolName,t3.id AS schoolId from t_course t1 JOIN t_teacher t2 ON t1.teacher_id=t2.id JOIN t_school t3 ON t2.school_id=t3.id WHERE t1.`status` !=2
AND t1.`name` LIKE CONCAT('%',#{name},'%')
AND t3.id=#{schoolId}
AND t1.status=#{status}

select t3.`name` AS teacherName,t2.period as signPeriod, t1.* from t_course as t1 ,t_student_course as t2, t_teacher as t3 where t1.id = t2.course_id and t1.teacher_id=t3.id
AND t2.student_id=#{studentId}
order by t1.curriculum_time desc
