mapper.select:
SELECT t1.id,t1.id AS entryFormId,t1.create_time AS entryTime,t1.exam_id AS examId,t1.student_id AS studentId,t4.`name` AS subjectName, t2.rank, t3.`name` AS studentName, t3.pinyin, t3.gender, DATE_FORMAT(t3.birthday,'%Y-%m-%d') birthday, t3.nation, t3.state, t3.major, t2.money AS entryFee, IF(t1.pay_status=1,'未缴费','已缴费') AS payFlag, t5.create_time AS payTime FROM t_entry_form t1 JOIN t_exam t2 ON t1.exam_id=t2.id JOIN t_student t3 ON t1.student_id=t3.id JOIN t_subject t4 ON t2.subject_id=t4.id LEFT JOIN t_order t5 ON t1.id=t5.entry_form_id AND t5.`status`=0 where 1 = 1
AND t2.school_id= #{schoolId}
AND t1.student_id= #{studentId}
AND t3.`name` LIKE CONCAT(#{studentName},'%')
AND t1.exam_id =#{examId}
AND t2.open_flag =#{openFlag}
AND t1.pay_status =#{payFlag}
ORDER BY t1.create_time DESC

SELECT t1.create_time AS entryTime, t4.`name` AS subjectName, t2.rank, t3.`name` AS studentName, t3.major FROM t_entry_form t1 JOIN t_exam t2 ON t1.exam_id=t2.id JOIN t_student t3 ON t1.student_id=t3.id JOIN t_subject t4 ON t2.subject_id=t4.id where 1 = 1
AND t1.student_id= #{studentId}
AND t1.pay_status= #{status}
ORDER BY t1.create_time DESC
