mapper.select:
select t1.path as jobPath,t2.status as status,t2.id as id,t3.name as courseName ,t3.id as courseid,t4.name as teacherName, t1.id as homeworkId,t1.create_time as publishDate,t2.path as path,t1.* from t_homework as t1,t_student_homework as t2, t_course as t3, t_teacher as t4 where t1.id = t2.homework_id and t1.course_id = t3.id and t3.teacher_id = t4.id
AND t2.`student_Id` =#{studentId}
AND t1.courseId=#{courseId}
AND t2.status=#{status}
order by t2.id desc
