mapper.select:
  SELECT s.id,s.phone,s.`name`,s.icon FROM t_course c
JOIN t_student_course sc ON c.id=sc.course_id
JOIN t_student s ON sc.student_id=s.id
WHERE c.teacher_id=#{teacherId} 

AND course_id=#{courseId}

SELECT p.id,p.`name`,p.create_time,p.path,s.`name` AS studentName FROM t_course c JOIN t_student_course sc ON c.id=sc.course_id JOIN t_student s ON sc.student_id=s.id JOIN t_product p ON sc.id=p.student_course_id WHERE c.teacher_id=1
AND course_id=#{courseId}
AND student_id=#{studentId}
